import React from 'react'
import './progressbar.css'
import Homepage from '../Hompage/homepage'
const ProgressBar=({progressvalue})=> {
 
    const ProgressBarBackgroundParentDiv={
        height:'1rem',
        backgroundColor: 'whitesmoke',
        width:'50%'
    }

    const ProgressBarMainChildDiv={
        height: '1rem',
        backgroundColor: 'red',
        textAlign: 'right'
    }

    
    Homepage.getElementById("main-Box-of-Website").addEventListener("click",()=>{
        if (Number((document.getElementById("myBar").getBoundingClientRect()).bottom) < 655){
            var i = 0;
            progressbarWidth(i)
        }
    })

    function progressbarWidth(i){
      if (i === 0) {
        i = 1;
        var elem = document.getElementById("myBar");
        var width = 1;
        var id = setInterval(frame, 10);
        function frame() {
          if (width >= progressvalue) {
            clearInterval(id);
            i = 0;
          } else {
            width++;
            elem.style.width = width + "%";
          }
        }
      }
    }
    
    return (
    <div  className="progressBar-Wrapper">
        <div  style={ProgressBarBackgroundParentDiv} className="ProgressBar-Background-Parent-Div">
            <div style={ProgressBarMainChildDiv}    className="ProgressBar-Main-Child-Div">
                <div id="myProgress" >
                    <div id="myBar"></div>
                </div>
            </div>
        </div>
    </div>
    )
}

export default ProgressBar;
