import React, { useState, useMemo} from 'react';
import './projectcontact.css';
import Select from 'react-select';
import countryList from 'react-select-country-list'
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


function Projectcontact() {
    const [countryvalue, countrysetValue] = useState('')
    const [projecttypevalue, projecttypesetValue] = useState('')
    
    const colourStyles = {
        option: (styles, { data, isDisabled, isFocused, isSelected }) => {
          // const color = chroma(data.color);
          return {
            ...styles,
            backgroundColor: "#4f5663",
            color: "rgb(175, 175, 175)"
          };
        }   
    };

    const options = useMemo(() => countryList().getData(), [])
    const projecttypeoption = [
        { label:"Web Development", value:"Web-Development"},
        { label:"Software Development", value:"Software-Development"},
        { label:"Anroid Development", value:"Anroid-Development"},
        { label:"IOS Development", value:"IOS-Development"},
        { label:"Other", value:"Other"}
    ]

    const [projectqueue, fill_details] = useState({
        firstname:"",
        lastname:"",
        contactno:"",
        altcontactno:"",
        email:"",
        projectinfo:"",
        message:"",
        note:""
    })

    const handleChange = (e) => {
        const { name, value } = e.target

        fill_details({
            ...projectqueue,
            [name]: value
        })
    }

    const countrychangeHandler = countryvalue => {
        countrysetValue(countryvalue)
        fill_details({
            ...projectqueue,
            'country': countryvalue['label'],
        })
    }

    const projecttypechangeHandler = projecttypevalue => {
        projecttypesetValue(projecttypevalue)
        fill_details({
            ...projectqueue,
            'projecttype': projecttypevalue['label']
        })
    }

    const submitform=()=>{    
        const { firstname, lastname, contactno, altcontactno, email, projectinfo, message, note } = projectqueue
        if (projectqueue){
            if(firstname && lastname && contactno  && email && projectinfo  && note) {
                axios.post("http://localhost:4000/projectqueue",projectqueue).then(res=>{
                    toast.info(res.data.alertmsg,{
                        position:"top-center"
                    })
                    
                    document.getElementById('firstname').value='';      projectqueue.firstname='';  
                    document.getElementById('lastname').value='';       projectqueue.lastname='';  
                    document.getElementById('contactno').value='';      projectqueue.contactno='';  
                    document.getElementById('altcontactno').value='';   projectqueue.altcontactno='';  
                    document.getElementById('email').value='';          projectqueue.email='';  
                    document.getElementById('projectinfo').value='';    projectqueue.projectinfo='';  
                    document.getElementById('message').value='';        projectqueue.message='';
                    document.getElementById('note').value='';           projectqueue.note ='';          
                    
                })
            } else {
                toast.warn("Please provide all the mentioned entries to contact us.",{
                    position:"top-center"
                })
            }   
        }
    }


    return (
        <>
        {console.log(projectqueue)}
        <div className="projectcontact-main">
            <button className="projectcontact-btn"><a href="#main-container-Wrapper-ContactUs-Page">Get to contact page</a></button>
        </div>


<div className="project-contactUS-main-container-Wrapper">
<div className="project-contactUS-main-container-Wrapper-Parent">

                <p className="project-contactUS-formParaHead"> Reach Us for Project!!</p>
            <div className="project-contactUS-formdivmaincontainer">

                <div className="project-contactUS-formdiv">
                <div className="mainproject-contactUS-form">
                {/* <form> */}
                    <div className="fromnamediv">
                    <div>
                     
                    <input type='text' className='project-input' id='fname' name="firstname"
                     value={projectqueue.firstname} onChange={handleChange} 
                     placeholder='First Name*' required />
                    </div>
                    <div>
            
                    <input type='text' className='project-input' id='lname' name="lastname" 
                    value={projectqueue.lastname} onChange={handleChange} 
                    placeholder='Last Name*' required />
                    </div>
                    </div>

                    <div className="fromcontactdiv">
                    <div>
                   
                    <input type='text' className='project-input' id='contact' name="contactno" 
                    value={projectqueue.contactno} onChange={handleChange}
                     placeholder='Contact Number*' required />
</div>
<div>
                    <input type='text' className='project-input' id='contact' name="altcontactno" 
                    value={projectqueue.altcontactno} onChange={handleChange}
                     placeholder='Alternate Number' />
                    </div>
                    <div>

                    <input type='text' className='project-input' id='email' name="email"
                     value={projectqueue.email} onChange={handleChange} 
                     placeholder='Email Address*' required />
                    </div>
                    </div>


                    <div className="country-dropdown">
                        <label htmlFor='country'  className="project-contact-us-label">Where Are You From*</label><br />
                        <Select options={options} className='project-input-dropdown' styles={colourStyles} value={countryvalue} onChange={countrychangeHandler}   />
                    </div>

                    <div className="project-information">
                    <label htmlFor='New' className='hiringLabel'>Project Info</label>
                    <input type="radio" id="New" name="projectinfo" value="New" onChange={handleChange} />
                    <label htmlFor="New">New </label>
                    <input type="radio" id="On-Going" name="projectinfo" value="On-Going" onChange={handleChange} />
                    <label htmlFor="On-Going">On Going</label>
                    <input type="radio" id="Other" name="projectinfo" value="Other" onChange={handleChange} />
                    <label htmlFor="Other">Other</label>

                </div>

                    <label htmlFor="type-of-project">Project Type</label>
                    <Select id="type-of-project" options={projecttypeoption} value={projecttypevalue} onChange={projecttypechangeHandler} />
                    <div className="message-div">
            
                    <textarea rows="5" cols="69" className='brief-intro-about-project' name="message" id='brief-info'
                     value={projectqueue.message} onChange={handleChange}  
                     placeholder='Brief Info about your Project*'
                    required />
                    </div>
                    
                    <textarea rows="5" cols="69" className='brief-intro-about-project-message' name="note" id='message'
                     value={projectqueue.note} onChange={handleChange}  
                     placeholder='Project Note' />

                    <div className='btndiv'>
                        <button className='submitbtn'
                         onClick={submitform} 
                         value="submit">Submit</button>
                    </div>
                    
                    </div>
                    </div>
                    </div>
                    
                      
                {/* </form> */}

                </div>
                </div>
          </>      
            
    )


}

export default Projectcontact;
